import React, { useEffect, useState } from 'react';

function AdminShopifyOrders() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [successMsg, setSuccessMsg] = useState('');
  const [sendingId, setSendingId] = useState(null);

  useEffect(() => {
    fetch('/api/shopify/orders')
      .then(res => res.json())
      .then(data => {
        setOrders(data);
        setLoading(false);
      })
      .catch(() => {
        setError('Erreur lors du chargement des commandes');
        setLoading(false);
      });
  }, []);

  const handleSendToNextera = async (orderId) => {
    setSendingId(orderId);
    setSuccessMsg('');
    try {
      const res = await fetch(`http://localhost:5001/api/shopify/send-to-nextera/${orderId}`, { method: 'POST' });
      const data = await res.json();
      if (res.ok) {
        setSuccessMsg(`Commande ${orderId} envoyée à Nextera !`);
      } else {
        setError(data.error || 'Erreur lors de l\'envoi à Nextera');
      }
    } catch (e) {
      setError('Erreur lors de l\'envoi à Nextera');
    }
    setSendingId(null);
  };

  return (
    <div style={{ padding: 24 }}>
      <h2>Commandes Shopify reçues</h2>
      {loading && <p>Chargement...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {successMsg && <p style={{ color: 'green' }}>{successMsg}</p>}
      <ul>
        {orders.map(order => (
          <li key={order.shopifyOrderId} style={{ marginBottom: 16, borderBottom: '1px solid #eee', paddingBottom: 8 }}>
            <strong>ID Shopify:</strong> {order.shopifyOrderId}<br />
            <strong>Date:</strong> {new Date(order.dateReception).toLocaleString()}<br />
            <details>
              <summary>Détails bruts</summary>
              <pre style={{ background: '#f8f8f8', padding: 8 }}>{JSON.stringify(order.data, null, 2)}</pre>
            </details>
            <button
              onClick={() => handleSendToNextera(order.shopifyOrderId)}
              disabled={sendingId === order.shopifyOrderId}
              style={{ marginTop: 8, background: '#1976d2', color: 'white', border: 'none', borderRadius: 4, padding: '8px 16px', cursor: 'pointer' }}
            >
              {sendingId === order.shopifyOrderId ? 'Envoi en cours...' : 'Envoyer à Nextera'}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminShopifyOrders;
